import{m as a,b as e,e as s,a6 as n}from"./chunks/framework.DpsY3XTg.js";const g=JSON.parse('{"title":"Synchronization","description":"Allows you to continue reading on your device from where you left off on another device.","frontmatter":{"title":"Synchronization","description":"Allows you to continue reading on your device from where you left off on another device.","footer":false},"headers":[],"relativePath":"manuals/guides/synchronization/index.md","filePath":"manuals/guides/synchronization/index.md","lastUpdated":1743672639000,"breadcrumbs":[{"text":"User manual","link":"/manuals/guides/getting-started/"},{"text":"Guides","items":[{"text":"Getting started","link":"/manuals/guides/getting-started/"},{"text":"Troubleshooting","link":"/manuals/guides/troubleshooting/","collapsed":true,"items":[{"text":"Common issues","link":"/manuals/guides/troubleshooting/common-issues/"},{"text":"Diagnosis","link":"/manuals/guides/troubleshooting/diagnosis/"}]},{"text":"Backups","link":"/manuals/guides/backups/"},{"text":"Tracking","link":"/manuals/guides/tracking/"},{"text":"Categories","link":"/manuals/guides/categories/"},{"text":"Synchronization","link":"/manuals/guides/synchronization/"}]},{"text":"Synchronization","link":"/manuals/guides/synchronization/"}]}'),o={name:"manuals/guides/synchronization/index.md"};function i(r,t,c,h,l,d){return s(),e("div",null,t[0]||(t[0]=[n('<h1 id="synchronization" tabindex="-1">Synchronization <a class="header-anchor" href="#synchronization" aria-label="Permalink to &quot;Synchronization&quot;">​</a></h1><p>Allows you to continue reading on your device from where you left off on another device.</p><div class="danger custom-block"><p class="custom-block-title">Unstable feature</p><p>This function is experimental. Please make sure you have a backup to avoid data loss.</p></div><p>List of official servers:</p><table><thead><tr><th>Domain</th><th>Location</th><th>Status</th></tr></thead><tbody><tr><td data-label="Domain">sync.kotatsu.app</td><td data-label="Location">Belarus</td><td data-label="Status"><img src="https://status.kotatsu.app/api/badge/1/status" alt=""></td></tr><tr><td data-label="Domain">moe.shirizu.org</td><td data-label="Location">Netherlands</td><td data-label="Status"><img src="https://status.kotatsu.app/api/badge/7/status" alt=""></td></tr></tbody></table><h2 id="what-is-synchronization" tabindex="-1">What is synchronization? <a class="header-anchor" href="#what-is-synchronization" aria-label="Permalink to &quot;What is synchronization?&quot;">​</a></h2><p>Synchronization is needed to store your collection of favorites, history and categories and have remote access to them. On a synchronized device, you can restore your manga collection in real time without loss. It also supports working across multiple devices. It is convenient for those who use several devices.</p><h2 id="how-does-synchronization-work" tabindex="-1">How does synchronization work? <a class="header-anchor" href="#how-does-synchronization-work" aria-label="Permalink to &quot;How does synchronization work?&quot;">​</a></h2><ul><li>An account is created and configured in the application where it will store data;</li><li>Synchronization starts. The data selected by the user is saved on the service and stored there under protection;</li><li>Another device connects and syncs with the service;</li><li>The uploaded data appears on the device connected to the account.</li></ul><h2 id="what-data-can-be-synchronized" tabindex="-1">What data can be synchronized? <a class="header-anchor" href="#what-data-can-be-synchronized" aria-label="Permalink to &quot;What data can be synchronized?&quot;">​</a></h2><ul><li>Favorites (with categories);</li><li>History.</li></ul><h2 id="how-do-i-sync-my-data" tabindex="-1">How do I sync my data? <a class="header-anchor" href="#how-do-i-sync-my-data" aria-label="Permalink to &quot;How do I sync my data?&quot;">​</a></h2><p>Go to <span class="shortcode navigation main_three_dots"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>dots-vertical</title><path d="M12,16A2,2 0 0,1 14,18A2,2 0 0,1 12,20A2,2 0 0,1 10,18A2,2 0 0,1 12,16M12,10A2,2 0 0,1 14,12A2,2 0 0,1 12,14A2,2 0 0,1 10,12A2,2 0 0,1 12,10M12,4A2,2 0 0,1 14,6A2,2 0 0,1 12,8A2,2 0 0,1 10,6A2,2 0 0,1 12,4Z"></path></svg><span class="name">Three dots</span></span> <span class="shortcode navigation direction"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>arrow-right-thin</title><path d="M14 16.94V12.94H5.08L5.05 10.93H14V6.94L19 11.94Z"></path></svg></span> <span class="shortcode navigation main_settings"><span class="name">Settings</span></span> <span class="shortcode navigation direction"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>arrow-right-thin</title><path d="M14 16.94V12.94H5.08L5.05 10.93H14V6.94L19 11.94Z"></path></svg></span> <span class="shortcode navigation services"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M22,13.5C22,15.26 20.7,16.72 19,16.96V20A2,2 0 0,1 17,22H13.2V21.7A2.7,2.7 0 0,0 10.5,19C9,19 7.8,20.21 7.8,21.7V22H4A2,2 0 0,1 2,20V16.2H2.3C3.79,16.2 5,15 5,13.5C5,12 3.79,10.8 2.3,10.8H2V7A2,2 0 0,1 4,5H7.04C7.28,3.3 8.74,2 10.5,2C12.26,2 13.72,3.3 13.96,5H17A2,2 0 0,1 19,7V10.04C20.7,10.28 22,11.74 22,13.5M17,15H18.5A1.5,1.5 0 0,0 20,13.5A1.5,1.5 0 0,0 18.5,12H17V7H12V5.5A1.5,1.5 0 0,0 10.5,4A1.5,1.5 0 0,0 9,5.5V7H4V9.12C5.76,9.8 7,11.5 7,13.5C7,15.5 5.75,17.2 4,17.88V20H6.12C6.8,18.25 8.5,17 10.5,17C12.5,17 14.2,18.25 14.88,20H17V15Z"></path></svg><span class="name">Services</span></span>, then select <strong>Synchronization</strong>. Enter your email address (even if you have not registered in the synchronization system, the authorization screen also acts as a registration screen), then come up with and enter a password.</p><div class="warning custom-block"><p class="custom-block-title">Attention</p><p>There is no password recovery from the account at the moment, if you forget the password, you will not be able to log into your account on other devices, so we recommend that you do not forget the password or write it down somewhere.</p></div><p>After the authorization/registration process, you will return back to the <strong>Content</strong> screen. To set up synchronization, select <strong>Synchronization</strong> again, and then you will go to system sync settings. Choose what you want to sync, history, favorites or all together, after which automatic synchronization to our server will begin.</p><h2 id="can-i-use-a-synchronization" tabindex="-1">Can I use a synchronization server on my hosting? <a class="header-anchor" href="#can-i-use-a-synchronization" aria-label="Permalink to &quot;Can I use a synchronization server on my hosting?&quot;">​</a></h2><p>Yes, you can use your synchronization server in the application by specifying its address (<span class="shortcode navigation main_three_dots"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>dots-vertical</title><path d="M12,16A2,2 0 0,1 14,18A2,2 0 0,1 12,20A2,2 0 0,1 10,18A2,2 0 0,1 12,16M12,10A2,2 0 0,1 14,12A2,2 0 0,1 12,14A2,2 0 0,1 10,12A2,2 0 0,1 12,10M12,4A2,2 0 0,1 14,6A2,2 0 0,1 12,8A2,2 0 0,1 10,6A2,2 0 0,1 12,4Z"></path></svg><span class="name">Three dots</span></span> <span class="shortcode navigation direction"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>arrow-right-thin</title><path d="M14 16.94V12.94H5.08L5.05 10.93H14V6.94L19 11.94Z"></path></svg></span> <span class="shortcode navigation main_settings"><span class="name">Settings</span></span> <span class="shortcode navigation direction"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>arrow-right-thin</title><path d="M14 16.94V12.94H5.08L5.05 10.93H14V6.94L19 11.94Z"></path></svg></span> <span class="shortcode navigation services"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M22,13.5C22,15.26 20.7,16.72 19,16.96V20A2,2 0 0,1 17,22H13.2V21.7A2.7,2.7 0 0,0 10.5,19C9,19 7.8,20.21 7.8,21.7V22H4A2,2 0 0,1 2,20V16.2H2.3C3.79,16.2 5,15 5,13.5C5,12 3.79,10.8 2.3,10.8H2V7A2,2 0 0,1 4,5H7.04C7.28,3.3 8.74,2 10.5,2C12.26,2 13.72,3.3 13.96,5H17A2,2 0 0,1 19,7V10.04C20.7,10.28 22,11.74 22,13.5M17,15H18.5A1.5,1.5 0 0,0 20,13.5A1.5,1.5 0 0,0 18.5,12H17V7H12V5.5A1.5,1.5 0 0,0 10.5,4A1.5,1.5 0 0,0 9,5.5V7H4V9.12C5.76,9.8 7,11.5 7,13.5C7,15.5 5.75,17.2 4,17.88V20H6.12C6.8,18.25 8.5,17 10.5,17C12.5,17 14.2,18.25 14.88,20H17V15Z"></path></svg><span class="name">Services</span></span> <span class="shortcode navigation direction"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>arrow-right-thin</title><path d="M14 16.94V12.94H5.08L5.05 10.93H14V6.94L19 11.94Z"></path></svg></span> <span class="shortcode navigation sync_settings"><span class="name">Synchronization settings</span></span> <span class="shortcode navigation direction"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><title>arrow-right-thin</title><path d="M14 16.94V12.94H5.08L5.05 10.93H14V6.94L19 11.94Z"></path></svg></span> <span class="shortcode navigation server_address"><span class="name">Server address</span></span>). Instructions for deploying the server are <a href="/dev/sync-server/">here</a>.</p>',17)]))}const u=a(o,[["render",i]]);export{g as __pageData,u as default};
